import React from 'react';
import monstar from './utilities/monstar.png';
import Draggable from 'react-draggable';

function AboutMe() {
  return (
    <div className="about-me">
        <h1>hi, im austin</h1>
        <p>i code from time to time. <br></br> unless generative ai wants to drive...</p>
        <Draggable positionOffset={{ x: '-50%', y: '-50%' }} onStop={(e, data) => {
            const { x, y } = data;
            const monstar = document.querySelector('.monstar');
            const screenWidth = window.innerWidth;
            const screenHeight = window.innerHeight;
            const imageWidth = monstar.offsetWidth;
            const imageHeight = monstar.offsetHeight;
            const minX = imageWidth / 2;
            const maxX = screenWidth - (imageWidth / 2);
            const minY = imageHeight / 2;
            const maxY = screenHeight - (imageHeight / 2);
            const clampedX = Math.min(Math.max(x, minX), maxX);
            const clampedY = Math.min(Math.max(y, minY), maxY);
            monstar.style.left = `${clampedX}px`;
            monstar.style.top = `${clampedY}px`;
        }}>
            <img src={monstar} alt="Monstar" className="monstar" />
        </Draggable>
        console.log(monstar);
    </div>
  );
}

export default AboutMe;
